import React, {useContext} from 'react';
import { Link } from "react-router-dom";
import GridContext from '../context/gridContext';

function ItemList({ data }) {
  const {isGrid} = useContext(GridContext);
  return (
    <div className="container">
      <div className="row">
        {data.items.map((item, index) => (
          <div key={index} className={isGrid ? "col-md-3 mb-5" : "mb-5"}>
            <Link to={`${data.card}/${item.id}`} className="card-link">
              <div className="card" style={isGrid ? { height: '400px' } : { height: '300px', width: '200px' }}>
                <img src={item.image.medium_url} className="card-img-top" alt={item.name} style={{ maxHeight: '90%', width: '100%' }} />
                <div className="card-body">
                  <h5 className="card-title">{item?.name}</h5>
                </div>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );

}

export default ItemList;
