export default function GetIssues({ page = 1 }) {
    const apiUrl = `http://localhost:3000/issues?page=${page}`;
    return fetch(apiUrl)()
      .then(res => res.json())
      .then(response => {
        const { data = [] } = response
        
        if (Array.isArray(data.results)) {
          console.log(data)
          return data.results;
        }
      })
}
