import React from 'react';
import Item from './card';
import { Result } from '../interfaces/results';

function ItemList({ data }) {

  return (
    <div className="container text-center bg-info justify-content-center" >  
      <div className="row align-items-center">
        {data.items.map((item, index) => (
          <Item data={item} index={index} card={data.card}></Item>
        ))}

      </div>
    </div>
  );

}
ItemList.propTypes = {
  data: PropTypes.arrayOf(Result).isRequired // Espera un array de objetos con la forma definida por ResultPropTypes
};

export default ItemList;
