import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';

import GetDetailInfo from '../services/getDetail';
import Detail from '../components/detail';


function DetailPage() {
    
    const [details, setDetail] = useState({});
    const { id } = useParams();
    const [isLoading, setLoaging] = useState(false);

    //obtener la informaci贸n de la ruta para hacer la petici贸n
    let location = useLocation().pathname.split("/");
    let information = location[1];
    let detail = location[2]
    
    useEffect(function () {

        setLoaging(true)
        console.log("hola")
        GetDetailInfo({information,detail,id }).then(data => {
            console.log("chao")
            setDetail(data);
            setLoaging(false);
        })

    }, [id])
    
    if (isLoading) return <i>Cargando...</i>

    console.log(details)
    return (
        <div>
            <Detail data={details}></Detail>
        </div>
    );
}

export default DetailPage;
