import React, { useEffect, useState } from 'react';
import { useLocation } from "react-router-dom";
import ItemList from '../components/listItems';
import GetListInfo from "../services/getListInfo";


function ListPage() {
    const INITIAL_PAGE =1 
    const [issues, setIssue] = useState([]);
    const [page, setPage] =useState(INITIAL_PAGE);
    const [isLoading, setLoaging] = useState(false);
    
    //obtener la informaci贸n de la ruta para hacer la petici贸n
    let locationInfo = useLocation().pathname.split("/")[1];
    
    let infoCard = ''
    locationInfo === 'issues'?infoCard = 'issue':infoCard = 'character' ;
    
    const newdata = {
        items: issues,
        card: infoCard
    };

    useEffect(function () {
        setLoaging(true)
        
        GetListInfo({information:locationInfo, page:page }).then(issues => {
            setIssue(issues);
            setLoaging(false);
        })
    }, [page, locationInfo])


    if (isLoading) return <i>Cargando...</i>

    return (
        <div>
            <ItemList data={newdata} />
        </div>
    );
}

export default ListPage;
