import React, { useEffect, useState } from 'react';
import { useLocation } from "react-router-dom";
import ItemList from '../components/listItems';
import GetListInfo from "../services/getListInfo";


function ListPage({page} = {page:1}) {

    const [issues, setIssue] = useState([]);
    const [isLoading, setLoaging] = useState(false);
    
    //obtener la informaci贸n de la ruta para hacer la petici贸n
    let location = useLocation().pathname.split("/")[1];
    
    let infoCard = ''
    location === 'issues'?infoCard = 'issue':infoCard = 'character' ;
    
    const newdata = {
        items: issues,
        card: infoCard
    };

    useEffect(function () {
        setLoaging(true)
        console.log(location)
        GetListInfo({ location: location, page }).then(issues => {
            setIssue(issues);
            setLoaging(false);
        })
    }, [page, location])

    if (isLoading) return <i>Cargando...</i>

    return (
        <div>
            <ItemList data={newdata} />
        </div>
    );
}

export default ListPage;
