import { useEffect, useState } from 'react';

export default function GetIssues({ page }) {
  
  const [issuesData, setIssuesData] = useState([]);

  const newData = {
    items: issuesData,
    card: 'issue'
  };

  useEffect(() => {
    const apiUrl = `http://localhost:3000/issues?page=${page}`;

    const fetchData = async () => {
      try {
        const response = await fetch(apiUrl);
        if (!response.ok) {
          throw new Error('Error al obtener los datos del servidor');
        }
        const data = await response.json();
        setIssuesData(data.results);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();

  }, []);
  return { newData };
}
