import React, { useEffect, useState } from 'react';
import { useLocation } from "react-router-dom";
import ItemList from '../components/listItems';
import GetIssues from "../services/getIssues";


function ListPage({ page }) {
    const [issues, setIssue] = useState([]);
    const [isLoading, setLoaging] = useState(false);

    //obtener la informaci贸n de la ruta para hacer la petici贸n
    let location = useLocation().pathname.split("/")[1];
    let infoCard = ''
    if (location === 'issues') {
        infoCard = 'issue'
    }
    else {
        infoCard = 'character'
    }
    const newdata = {
        items: issues,
        card: infoCard
    };

    useEffect(function () {
        setLoaging(true)
        GetIssues({ information: location, page }).then(issues => {
            setIssue(issues);
            setLoaging(false);
        })
    }, [page, location])

    if (isLoading) return <i>Cargando...</i>

    return (
        <div>
            <ItemList data={newdata} />
        </div>
    );
}

export default ListPage;
